    $(document).on("click",".section",function(){
        var choose = $(this).attr("name")
        var answer = $(this).attr("answer")
        var number = $(this).attr("number")
        //选错了
        if(choose != answer){
            //背景变成红色
            $(this).css("background-color","red")
            //加入错题集
            falseArry.push(number)
            //下一题
            if((mode == "all" && num!=data.length-1) || (mode == "notAll" && num != questionNum) ){
                num++
                count--
                initCount(count)
                setTimeout(function(){
                 initQUestion(randoms[num])
                },500)
            }if((mode == "all" && num==data.length) || (mode == "notAll" && num == questionNum) ){
                setTimeout(function(){
                    initFalse(falseNum)
                },1000)
            }
        }
        if(choose === answer){
           //背景变成绿色
           $(this).css("background-color","green")
           //下一题
           if((mode == "all" && num!=data.length-1) || (mode == "notAll" && num != questionNum) ){
               num++
               setTimeout(function(){
                initQUestion(randoms[num])
               },500)
           } if((mode == "all" && num==data.length) || (mode == "notAll" && num == questionNum) ){
            setTimeout(function(){
                initFalse(falseNum)
            },1000)
            }
        }
    })
