    //错题集中的上一题下一题切换
    $(document).on("click","#last",function(){
        if(falseNum != 0 ){
            falseNum -- 
            initFalse(falseNum)
        }
    })
    $(document).on("click","#next",function(){
        if(falseNum != falseArry.length-1 ){
            falseNum ++ 
            initFalse(falseNum)
        }
    })
